// src/components/PostureCard.tsx
import React from "react";
import { PosturaData } from "../types";
import { PieChart, Pie, Cell } from "recharts";

interface Props {
  postura: PosturaData;
}

const PostureCard: React.FC<Props> = ({ postura }) => {
  const pieData = [
    { name: "Correcta", value: postura.porcentaje_correcta },
    { name: "Incorrecta", value: postura.porcentaje_incorrecta },
  ];
  const COLORS = ["#22c55e", "#f43f5e"];

  const alertColor = postura.porcentaje_incorrecta > 70 ? "text-red-600" : "text-yellow-600";

  return (
    <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg shadow">
      <h2 className="text-2xl font-extrabold mb-2 flex items-center gap-2">üßç‚Äç‚ôÇÔ∏è Postura Actual</h2>
      <p className="text-xl font-bold text-blue-700 mb-2">{postura.actual}</p>
      <p className="mb-2 text-gray-700">Transiciones a mala postura: <strong>{postura.transiciones_malas}</strong></p>

      <div className="flex justify-around items-center">
        <PieChart width={100} height={100}>
          <Pie
            data={pieData}
            cx={50}
            cy={50}
            innerRadius={25}
            outerRadius={40}
            dataKey="value"
          >
            {pieData.map((_, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
        </PieChart>
        <div>
          <p className="text-green-600">‚úÖ Correcta: {postura.porcentaje_correcta.toFixed(1)}%</p>
          <p className={`${alertColor}`}>‚ùå Incorrecta: {postura.porcentaje_incorrecta.toFixed(1)}%</p>
        </div>
      </div>

      <p className="text-gray-800 mt-2">ü™ë Sentado: {postura.tiempo_sentado}s / üßç‚Äç‚ôÄÔ∏è Parado: {postura.tiempo_parado}s</p>
      <p className="text-rose-600">üö® Alertas: {postura.alertas_enviadas}</p>
    </div>
  );
};

export default PostureCard;
