import React from "react";
import { PosturaData } from "../types";
import { PieChart, Pie, Cell } from "recharts";

interface Props {
  postura: PosturaData;
}

const PostureCard: React.FC<Props> = ({ postura }) => {
  const pieData = [
    { name: "Correcta", value: postura.porcentaje_correcta },
    { name: "Incorrecta", value: postura.porcentaje_incorrecta },
  ];
  const COLORS = ["#16a34a", "#dc2626"];

  return (
    <div className="p-4 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-2">üßç‚Äç‚ôÇÔ∏è Postura Actual</h2>
      <p className="text-lg font-bold text-blue-700">{postura.actual}</p>
      <p className="mt-2 text-gray-600">Transiciones a mala postura: <strong>{postura.transiciones_malas}</strong></p>

      <div className="flex justify-around items-center">
        <PieChart width={100} height={100}>
          <Pie
            data={pieData}
            cx={50}
            cy={50}
            innerRadius={25}
            outerRadius={40}
            dataKey="value"
          >
            {pieData.map((_, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
        </PieChart>
        <div>
          <p className="text-green-600">‚úÖ Correcta: {postura.porcentaje_correcta.toFixed(1)}%</p>
          <p className="text-red-600">‚ùå Incorrecta: {postura.porcentaje_incorrecta.toFixed(1)}%</p>
        </div>
      </div>

      <p className="text-gray-700 mt-2">ü™ë Sentado: {postura.tiempo_sentado}s / üßç‚Äç‚ôÄÔ∏è Parado: {postura.tiempo_parado}s</p>
      <p className="text-rose-600">üö® Alertas: {postura.alertas_enviadas}</p>
    </div>
  );
};

export default PostureCard;
