import React from "react";
import { HistorialPosturalItem } from "../types";
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid
} from "recharts";

interface Props {
  historial: HistorialPosturalItem[];
}

const POSTURAS_FIJAS = [
  "sentado erguido",
  "inclinación hacia adelante",
  "inclinación hacia atrás",
  "inclinación hacia la izquierda",
  "inclinación hacia la derecha",
  "mentón en mano",
  "piernas cruzadas",
  "rodillas elevadas o muy bajas",
  "hombros encogidos",
  "brazos sin apoyo",
  "cabeza adelantada",
  "slouching",
  "sentado al borde del asiento"
];

const HistoryChart: React.FC<Props> = ({ historial }) => {
  const data = POSTURAS_FIJAS.map((nombre) => {
    const found = historial.find((item) => item.nombre === nombre);
    return {
      nombre,
      conteo: found ? found.conteo : 0,
    };
  });

  return (
    <div className="w-full h-72 p-2">
      <h2 className="font-bold text-lg mb-2">📊 Historial de Posturas</h2>
      <ResponsiveContainer width="100%" height="100%">
        <BarChart data={data} margin={{ top: 10, right: 10, left: 10, bottom: 60 }}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="nombre" angle={-30} textAnchor="end" interval={0} height={60} />
          <YAxis allowDecimals={false} />
          <Tooltip formatter={(value: any) => [`conteo : ${value}`]} />
          <Bar dataKey="conteo" fill="#3b82f6" radius={[4, 4, 0, 0]} />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default HistoryChart;
