// src/components/PostureCard.tsx
import React from "react";
import { PosturaData } from "../types";
import { PieChart, Pie, Cell } from "recharts";

interface Props {
  postura: PosturaData;
}

const PostureCard: React.FC<Props> = ({ postura }) => {
  const pieData = [
    { name: "Correcta", value: postura.porcentaje_correcta },
    { name: "Incorrecta", value: postura.porcentaje_incorrecta },
  ];

  const getColor = (incorrecta: number) => {
    if (incorrecta > 70) return "text-red-600";
    if (incorrecta > 40) return "text-yellow-600";
    return "text-green-600";
  };

  const riesgo = (incorrecta: number) => {
    if (incorrecta > 70) return "üìâ Riesgo alto de fatiga postural";
    if (incorrecta > 40) return "‚ö†Ô∏è Riesgo moderado";
    return "‚úÖ Riesgo bajo";
  };

  return (
    <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg shadow space-y-2">
      <h2 className="text-2xl font-extrabold">üßç‚Äç‚ôÇÔ∏è Postura Actual</h2>
      <p className="text-xl font-bold text-blue-700">{postura.actual}</p>
      <p className="text-gray-700">Transiciones a mala postura: <strong>{postura.transiciones_malas}</strong></p>

      <div className="flex flex-col items-start gap-2 max-w-[320px]">
        <div className="flex items-center gap-2">
          <PieChart width={100} height={100}>
            <Pie
              data={pieData}
              cx={50}
              cy={50}
              innerRadius={25}
              outerRadius={40}
              dataKey="value"
            >
              <Cell fill="#22c55e" />
              <Cell fill="#f43f5e" />
            </Pie>
          </PieChart>
          <div>
            <p className="text-green-600">‚úÖ Correcta: {postura.porcentaje_correcta.toFixed(1)}%</p>
            <p className={getColor(postura.porcentaje_incorrecta)}>‚ùå Incorrecta: {postura.porcentaje_incorrecta.toFixed(1)}%</p>
          </div>
        </div>
        <p className={`text-sm ${getColor(postura.porcentaje_incorrecta)}`}>{riesgo(postura.porcentaje_incorrecta)}</p>
      </div>

      <hr className="my-2 border-gray-300" />

      <div className="text-gray-800 space-y-1">
        <p>ü™ë Sentado: {postura.tiempo_sentado}s / üßç‚Äç‚ôÄÔ∏è Parado: {postura.tiempo_parado}s</p>
        <p className="text-rose-600">üö® Alertas: {postura.alertas_enviadas}</p>
      </div>
    </div>
  );
};

export default PostureCard;
