import { useEffect, useState } from "react";
import { PosturaMessage } from "./types";
import PostureCard from "./components/PostureCard";
import SessionInfo from "./components/SessionInfo";
import HistoryChart from "./components/HistoryChart";
import Header from "./components/Header";
<Header
  paciente={{ nombre: "Juan Pérez", edad: 32, diagnostico: "Lumbalgia crónica" }}
  tiempoActual={new Date().toLocaleString()}
  estadoSesion="En curso"
/>


export default function App() {
  const [data, setData] = useState<PosturaMessage | null>(null);

  useEffect(() => {
    const interval = setInterval(() => {
      // 🧪 Simulación de WebSocket con datos randomizados
      const simulated: PosturaMessage = {
        timestamp: new Date().toISOString(),
        session: {
          intervalo_segundos: 600,
          tiempo_transcurrido: Math.floor(Math.random() * 600),
          modo: "monitor_activo",
        },
        postura: {
          actual: "mentón en mano",
          transiciones_malas: Math.floor(Math.random() * 10),
          porcentaje_correcta: Math.random() * 100,
          porcentaje_incorrecta: Math.random() * 100,
          tiempo_parado: Math.floor(Math.random() * 200),
          tiempo_sentado: Math.floor(Math.random() * 400),
          alertas_enviadas: Math.floor(Math.random() * 5),
        },
        historial_postural: [
          { nombre: "sentado erguido", conteo: 65 },
          { nombre: "mentón en mano", conteo: 12 },
          { nombre: "slouching", conteo: 5 },
          { nombre: "inclinación adelante", conteo: 3 },
        ],
      };

      setData(simulated);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  if (!data) return <div className="p-6 text-center text-gray-500">Cargando métricas...</div>;

  return (
    <main className="min-h-screen bg-gray-100 p-6 grid gap-6 grid-cols-1 md:grid-cols-3">
      <section className="md:col-span-2 space-y-4">
        <PostureCard postura={data.postura} />
        <HistoryChart historial={data.historial_postural} />
      </section>
      <SessionInfo session={data.session} />
    </main>
  );
}
