import React from "react";
import { HistorialPosturalItem } from "../types";
import { ResponsiveContainer, BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid } from "recharts";

interface Props {
  historial: HistorialPosturalItem[];
}

const HistoryChart: React.FC<Props> = ({ historial }) => {
  return (
    <div className="p-4 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-2">ðŸ“Š Historial de Posturas</h2>
      <ResponsiveContainer width="100%" height={250}>
        <BarChart data={historial}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="nombre" interval={0} angle={-20} textAnchor="end" height={70} />
          <YAxis allowDecimals={false} />
          <Tooltip />
          <Bar dataKey="conteo" fill="#3b82f6" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default HistoryChart;
